import i18n from "i18next";
import { initReactI18next } from "react-i18next";

const resources = {
tr: {
    translation: {
    "Dashboard": "Kontrol Paneli",
    "Equipments": "Ekipmanlar",
    "MyReservations": "Rezervasyonlarım",
    "Management": "Yönetim",
    "Reports": "Raporlar",
    "WeeklyCalendar": "Haftalık Rezervasyon Takvimi",
    "Settings": "Ayarlar",
    "Language": "Dil",
    "Save": "Kaydet"
    }
},
en: {
    translation: { "Dashboard": "Dashboard",
    "Equipments": "Equipments",
    "MyReservations": "My Reservations",
    "Management": "Management",
    "Reports": "Reports",
    "WeeklyCalendar": "Weekly Reservation Calendar",
    "Settings": "Settings",
    "Language": "Language",
    "Save": "Save"
    }
},
de: {
    translation: {
    "Dashboard": "Kontrollzentrum",
    "Equipments": "Ausrüstung",
    "MyReservations": "Meine Reservierungen",
    "Management": "Verwaltung",
    "Reports": "Berichte",
    "WeeklyCalendar": "Wöchentlicher Reservierungskalender",
    "Settings": "Einstellungen",
    "Language": "Sprache",
    "Save": "Speichern"
    }
}
};

i18n
.use(initReactI18next)
.init({
    resources,
    lng: "tr", // Başlangıç dili Türkçe olsun
    interpolation: {
    escapeValue: false
    }
});

export default i18n;
