                <button 
                    onClick={() => setAktifSekme('lab')}
                    className={`px-4 py-2 font-medium rounded transition ${aktifSekme === 'lab' ? 'bg-blue-100 text-blue-700' : 'text-gray-600 hover:bg-gray-50'}`}
