                                <tr>
                                    <th className="px-6 py-3">KullanÄ±cÄ± Bilgisi</th>
                                    <th className="px-6 py-3">E-Posta</th>
                                    <th className="px-6 py-3 text-right">Ä°ÅŸlemler</th>
