import axios from 'axios';
import { Download, File, FileText, Filter, Plus, Trash2 } from 'lucide-react';
import { useEffect, useState } from 'react';
import RaporYukleModal from './RaporYukleModal';

const API_URL = "http://localhost:5000/api";

// 1. ADIM: S√ñZL√úK OLU≈ûTURUYORUZ
const translations = {
    tr: {
        title: "Laboratuvar Raporlarƒ±",
        desc: "Cihaz analizleri, bakƒ±m ve test raporlarƒ±.",
        uploadBtn: "Rapor Y√ºkle",
        filterLab: "Laboratuvar Filtrele",
        filterEq: "Ekipman Filtrele",
        allLabs: "T√ºm Laboratuvarlar",
        allEq: "T√ºm Cihazlar",
        colFile: "Dosya",
        colTitle: "Ba≈ülƒ±k",
        colUnit: "ƒ∞lgili Birim",
        colUploader: "Y√ºkleyen",
        colDate: "Tarih",
        colAction: "ƒ∞≈ülem",
        noData: "Bu kriterlerde rapor bulunamadƒ±.",
        confirmDelete: "Bu raporu silmek istediƒüine emin misin?",
        deleteError: "Silme ba≈üarƒ±sƒ±z."
    },
    en: {
        title: "Laboratory Reports",
        desc: "Device analysis, maintenance, and test reports.",
        uploadBtn: "Upload Report",
        filterLab: "Filter Laboratory",
        filterEq: "Filter Equipment",
        allLabs: "All Laboratories",
        allEq: "All Devices",
        colFile: "File",
        colTitle: "Title",
        colUnit: "Related Unit",
        colUploader: "Uploader",
        colDate: "Date",
        colAction: "Action",
        noData: "No reports found matching criteria.",
        confirmDelete: "Are you sure you want to delete this report?",
        deleteError: "Deletion failed."
    },
    de: {
        title: "Laborberichte",
        desc: "Ger√§teanalysen, Wartungs- und Testberichte.",
        uploadBtn: "Bericht hochladen",
        filterLab: "Labor filtern",
        filterEq: "Ger√§te filtern",
        allLabs: "Alle Labore",
        allEq: "Alle Ger√§te",
        colFile: "Datei",
        colTitle: "Titel",
        colUnit: "Zugeh√∂rige Einheit",
        colUploader: "Hochlader",
        colDate: "Datum",
        colAction: "Aktion",
        noData: "Keine Berichte gefunden.",
        confirmDelete: "Sind Sie sicher, dass Sie diesen Bericht l√∂schen m√∂chten?",
        deleteError: "L√∂schen fehlgeschlagen."
    }
};

// language prop'unu aldƒ±k üëá
const Raporlar = ({ user, language = 'tr' }) => {
    const [raporlar, setRaporlar] = useState([]);
    const [lablar, setLablar] = useState([]);
    const [ekipmanlar, setEkipmanlar] = useState([]);

    const [selectedLab, setSelectedLab] = useState('');
    const [selectedEkipman, setSelectedEkipman] = useState('');
    const [isUploadOpen, setIsUploadOpen] = useState(false);

    // Se√ßili dile g√∂re metinleri al
    const t = translations[language] || translations['tr'];

    useEffect(() => {
        fetchRaporlar();
        axios.get(`${API_URL}/labs`).then(res => setLablar(res.data));
    }, []);

    useEffect(() => {
        fetchRaporlar();
        if (selectedLab) {
            axios.get(`${API_URL}/ekipman/by-lab/${selectedLab}`).then(res => setEkipmanlar(res.data));
        } else {
            setEkipmanlar([]);
            setSelectedEkipman('');
        }
    }, [selectedLab, selectedEkipman]);

    const fetchRaporlar = async () => {
        try {
            let url = `${API_URL}/rapor`;
            const params = [];
            if (selectedLab) params.push(`labId=${selectedLab}`);
            if (selectedEkipman) params.push(`ekipmanId=${selectedEkipman}`);
            if (params.length > 0) url += `?${params.join('&')}`;

            const res = await axios.get(url);
            setRaporlar(res.data);
        } catch (error) {
            console.error("Raporlar √ßekilemedi", error);
        }
    };

    const handleDownload = (id, fileName) => {
        axios({
            url: `${API_URL}/rapor/download/${id}`,
            method: 'GET',
            responseType: 'blob',
        }).then((response) => {
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', fileName);
            document.body.appendChild(link);
            link.click();
            link.remove();
        }).catch(() => alert("Dosya indirilemedi."));
    };

    const handleDelete = async (id) => {
        if(!window.confirm(t.confirmDelete)) return; // √áeviri kullanƒ±ldƒ±
        try {
            await axios.delete(`${API_URL}/rapor/${id}`);
            setRaporlar(prev => prev.filter(r => r.raporID !== id));
        } catch (error) {
            alert(t.deleteError);
        }
    };

    return (
        <div className="bg-white p-6 rounded-lg shadow h-full flex flex-col">
            <div className="flex justify-between items-center mb-6 border-b pb-4">
                <div>
                    <h2 className="text-2xl font-bold text-gray-800 flex items-center gap-2">
                        <FileText className="text-blue-600"/> {t.title}
                    </h2>
                    <p className="text-sm text-gray-500">{t.desc}</p>
                </div>
                <button 
                    onClick={() => setIsUploadOpen(true)}
                    className="bg-blue-600 text-white px-4 py-2 rounded-lg font-bold flex items-center hover:bg-blue-700 transition shadow"
                >
                    <Plus className="mr-2 h-5 w-5" /> {t.uploadBtn}
                </button>
            </div>

            <div className="bg-blue-50 p-4 rounded-lg mb-6 flex gap-4 items-end border border-blue-100">
                <div className="flex-1">
                    <label className="text-xs font-bold text-gray-500 uppercase mb-1 block">{t.filterLab}</label>
                    <select 
                        className="w-full p-2 border rounded-md text-sm bg-white"
                        value={selectedLab}
                        onChange={e => setSelectedLab(e.target.value)}
                    >
                        <option value="">{t.allLabs}</option>
                        {lablar.map(l => <option key={l.labID} value={l.labID}>{l.labAdi}</option>)}
                    </select>
                </div>
                <div className="flex-1">
                    <label className="text-xs font-bold text-gray-500 uppercase mb-1 block">{t.filterEq}</label>
                    <select 
                        className="w-full p-2 border rounded-md text-sm bg-white"
                        value={selectedEkipman}
                        onChange={e => setSelectedEkipman(e.target.value)}
                        disabled={!selectedLab}
                    >
                        <option value="">{t.allEq}</option>
                        {ekipmanlar.map(e => <option key={e.ekipmanID} value={e.ekipmanID}>{e.ekipmanAdi}</option>)}
                    </select>
                </div>
                <div className="p-2 text-blue-400">
                    <Filter />
                </div>
            </div>

            <div className="overflow-x-auto flex-grow">
                <table className="w-full text-sm text-left text-gray-500">
                    <thead className="text-xs text-gray-700 uppercase bg-gray-100">
                        <tr>
                            <th className="px-6 py-3">{t.colFile}</th>
                            <th className="px-6 py-3">{t.colTitle}</th>
                            <th className="px-6 py-3">{t.colUnit}</th>
                            <th className="px-6 py-3">{t.colUploader}</th>
                            <th className="px-6 py-3">{t.colDate}</th>
                            <th className="px-6 py-3 text-right">{t.colAction}</th>
                        </tr>
                    </thead>
                    <tbody>
                        {raporlar.length === 0 ? (
                            <tr><td colSpan="6" className="text-center py-10 text-gray-400">{t.noData}</td></tr>
                        ) : (
                            raporlar.map(rapor => (
                                <tr key={rapor.raporID} className="bg-white border-b hover:bg-gray-50 transition">
                                    <td className="px-6 py-4">
                                        <div className="bg-blue-100 p-2 rounded w-fit text-blue-600">
                                            <File size={20} />
                                        </div>
                                    </td>
                                    <td className="px-6 py-4">
                                        <div className="font-bold text-gray-800">{rapor.baslik}</div>
                                        <div className="text-xs text-gray-400">{rapor.orijinalDosyaAdi}</div>
                                    </td>
                                    <td className="px-6 py-4">
                                        <div className="text-xs font-semibold text-blue-700">{rapor.labAdi}</div>
                                        <div className="text-xs text-gray-500">{rapor.ekipmanAdi}</div>
                                    </td>
                                    <td className="px-6 py-4 text-gray-600">{rapor.yukleyen}</td>
                                    <td className="px-6 py-4 text-gray-500">
                                        {/* Tarihi dile g√∂re formatla */}
                                        {new Date(rapor.yuklemeTarihi).toLocaleDateString(language === 'en' ? 'en-US' : (language === 'de' ? 'de-DE' : 'tr-TR'))}
                                    </td>

                                    <td className="px-6 py-4 text-right flex justify-end gap-2">
                                        <button 
                                            onClick={() => handleDownload(rapor.raporID, rapor.orijinalDosyaAdi)}
                                            className="text-blue-600 hover:bg-blue-100 p-2 rounded-full transition"
                                        >
                                            <Download size={18} />
                                        </button>

                                        {(user.role === 'Y√∂netici' || user.id === rapor.kullaniciID) && (
                                            <button 
                                                onClick={() => handleDelete(rapor.raporID)}
                                                className="text-red-600 hover:bg-red-100 p-2 rounded-full transition"
                                            >
                                                <Trash2 size={18} />
                                            </button>
                                        )}
                                    </td>
                                </tr>
                            ))
                        )}
                    </tbody>
                </table>
            </div>

            {/* Modala da dil bilgisini g√∂nderiyoruz */}
            <RaporYukleModal 
                isOpen={isUploadOpen} 
                onClose={() => setIsUploadOpen(false)} 
                user={user}
                onSuccess={fetchRaporlar}
                language={language} 
            />
        </div>
    );
};

export default Raporlar;
