import FullCalendar from '@fullcalendar/react';
import timeGridPlugin from '@fullcalendar/timegrid';
import axios from 'axios';
import { useEffect, useState } from 'react';
import { Navigate, Route, BrowserRouter as Router, Routes, useNavigate } from 'react-router-dom';
// İkonları 'lucide-react' paketinden tek tek import ediyoruz
import { CalendarCheck, Cpu, LayoutDashboard, LogOut, Shield } from 'lucide-react';

// Register bileşenini import et
// DİKKAT: Register.js dosyasının nerede olduğuna göre bu yolu düzeltmelisin!
// Eğer src/components/Register.js ise:
import Register from './Register';
// Eğer src/Register.js ise (components klasörü yoksa):
// import Register from './Register';

const API_URL = "http://localhost:5000/api";

//İkonları kolay kullanmak için bir obje
const icons = {
LayoutDashboard,
Cpu,
CalendarCheck,
Shield,
LogOut
};

// --- MOCK DATA ---
const mockEquipment = [
    { id: 1, name: 'Osiloskop Tektronix TBS1052B', description: '50 MHz, 2 Kanal', status: 'Müsait', location: 'Elektronik Lab' },
    { id: 2, name: 'Spektrum Analizörü Rigol DSA815', description: '1.5 GHz', status: 'Müsait', location: 'RF Lab' },
    { id: 3, name: 'Güç Kaynağı Keysight E3631A', description: '6V/5A & ±25V/1A', status: 'Bakımda', location: 'Elektronik Lab' },
    { id: 4, name: 'Sinyal Jeneratörü Siglent SDG1032X', description: '30MHz', status: 'Arızalı', location: 'Elektronik Lab' },
    { id: 5, name: 'Isıl Çevrim Kabini', description: '-40°C to 150°C', status: 'Müsait', location: 'Mekanik Test Alanı' },
];
const mockReservations = [
    { id: 1, equipmentId: 1, userId: 1, userFullName: 'Ali Veli', startTime: '2025-10-06T09:00:00', endTime: '2025-10-06T11:00:00', status: 'Confirmed' },
    { id: 2, equipmentId: 2, userId: 3, userFullName: 'Fatma Kaya', startTime: '2025-10-07T14:00:00', endTime: '2025-10-07T17:00:00', status: 'Confirmed' },
    { id: 3, equipmentId: 1, userId: 1, userFullName: 'Ali Veli', startTime: '2025-10-08T10:00:00', endTime: '2025-10-08T12:00:00', status: 'Confirmed' },
    { id: 4, equipmentId: 5, userId: 3, userFullName: 'Fatma Kaya', startTime: '2025-10-09T13:00:00', endTime: '2025-10-09T16:00:00', status: 'Confirmed' },
];
const statusColors = {
    'Müsait': 'bg-green-100 text-green-800',
    'Bakımda': 'bg-yellow-100 text-yellow-800',
    'Arızalı': 'bg-red-100 text-red-800',
};
const reservationColors = [
    'bg-blue-200 border-blue-400',
    'bg-indigo-200 border-indigo-400',
    'bg-purple-200 border-purple-400',
    'bg-pink-200 border-pink-400'
];

// --- LOGIN PAGE ---
const LoginPage = ({ onLogin }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const response = await axios.post(`${API_URL}/auth/giris`, {
                Email: email,
                SifreHash: password
            });

            const userData = {
                id: response.data.id,
                fullName: `${response.data.ad} ${response.data.soyad}`,
                email: response.data.email,
                role: response.data.rolId === 1 ? 'Öğrenci' : 'Laboratuvar Yöneticisi'
            };

            onLogin(userData);
            navigate('/'); // Başarılı girişte anasayfaya at

        } catch (err) {
            const errorMsg = err.response?.data || 'Giriş başarısız.';
            setError(errorMsg);
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center">
            <div className="w-full max-w-md bg-white p-8 rounded-md shadow-2xl">
                <div className="text-center mb-6">
                    <h1 className="text-3xl font-extrabold text-gray-800">Test Laboratuvarı Sistemi</h1>
                    <p className="text-gray-500 mt-2">Lütfen devam etmek için giriş yapın.</p>
                </div>
                <form onSubmit={handleSubmit}>
                    {error && <p className="bg-red-100 text-red-700 p-3 rounded mb-4 text-sm">{error}</p>}
                    <div className="mb-4">
                        <label className="block text-gray-700 text-sm font-semibold mb-2">E-posta</label>
                        <input type="email" value={email} onChange={e => setEmail(e.target.value)} className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500" required />
                    </div>
                    <div className="mb-6">
                        <label className="block text-gray-700 text-sm font-semibold mb-2">Şifre</label>
                        <input type="password" value={password} onChange={e => setPassword(e.target.value)} className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500" required />
                    </div>
                    <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg transition duration-300">
                        Giriş Yap
                    </button>
                </form>

                <div className="mt-4 text-center border-t pt-4">
                    <p className="text-sm text-gray-600">
                        Hesabın yok mu?
                        <button
                            onClick={() => navigate('/register')}
                            className="ml-2 text-blue-600 hover:underline font-bold focus:outline-none"
                        >
                            Kayıt Ol
                        </button>
                    </p>
                </div>
            </div>
        </div>
    );
};

// --- DİĞER BİLEŞENLER ---
const Sidebar = ({ user, activePage, onNavigate }) => {
    const navItems = [
        { name: 'Kontrol Paneli', icon: 'LayoutDashboard', page: 'dashboard', roles: ['Öğrenci', 'Laboratuvar Yöneticisi'] }, // Rol isimlerini Backend ile eşitledik (Öğrenci vs.)
        { name: 'Ekipmanlar', icon: 'Cpu', page: 'equipment', roles: ['Öğrenci', 'Laboratuvar Yöneticisi'] },
        { name: 'Rezervasyonlarım', icon: 'CalendarCheck', page: 'my-reservations', roles: ['Öğrenci', 'Laboratuvar Yöneticisi'] },
        { name: 'Yönetim', icon: 'Shield', page: 'admin', roles: ['Laboratuvar Yöneticisi'] }
    ];

    const Icon = ({ name }) => { const LucideIcon = icons[name]; return LucideIcon ? <LucideIcon className="h-5 w-5 mr-3" /> : null; };

    return (
        <div className="w-64 bg-white border-r border-gray-200 flex flex-col">
            <div className="p-4 border-b border-gray-200"><h1 className="text-xl font-bold text-gray-800">Test Lab Sistemi</h1></div>
            <nav className="flex-grow p-4"><ul>{navItems.filter(item => item.roles.includes(user.role)).map(item => (<li key={item.name}><a href="#" onClick={(e) => {e.preventDefault(); onNavigate(item.page)}} className={`flex items-center px-4 py-2.5 my-1 rounded-lg transition-colors duration-200 ${activePage === item.page ? 'bg-blue-50 text-blue-600 font-semibold' : 'text-gray-600 hover:bg-gray-100'}`}><Icon name={item.icon} />{item.name}</a></li>))}</ul></nav>
        </div>
    );
};

const Header = ({ user, onLogout }) => {
    const Icon = ({ name, className }) => { const LucideIcon = icons[name]; return LucideIcon ? <LucideIcon className={className} /> : null; };
    return (<header className="h-16 bg-white border-b border-gray-200 flex items-center justify-end px-6"><div className="flex items-center"><div className="text-right mr-4"><p className="font-semibold text-gray-800">{user.fullName}</p><p className="text-xs text-gray-500">{user.role}</p></div><button onClick={onLogout} className="p-2 rounded-full hover:bg-gray-100 transition"><Icon name="LogOut" className="w-5 h-5 text-gray-600" /></button></div></header>);
};

const ReservationCalendar = () => {
    const calendarEvents = mockReservations.map(res => {
        const equipment = mockEquipment.find(e => e.id === res.equipmentId);
        const color = reservationColors[res.equipmentId % reservationColors.length];
        return {
            id: res.id.toString(),
            title: `${equipment.name} (${res.userFullName})`,
            start: new Date(res.startTime),
            end: new Date(res.endTime),
            backgroundColor: color.split(' ')[0].replace('bg-', '#').replace('-200', ''),
            borderColor: color.split(' ')[1].replace('border-', '#').replace('-400', '')
        };
    });
    return (<div className="bg-white p-6 rounded-lg shadow"><h2 className="text-xl font-bold text-gray-800 mb-4">Takvim</h2><FullCalendar plugins={[timeGridPlugin]} initialView="timeGridWeek" events={calendarEvents} locale='tr' height="auto" headerToolbar={{left: 'prev,next today', center: 'title', right: 'timeGridWeek,timeGridDay'}} slotMinTime="08:00:00" slotMaxTime="19:00:00" slotLabelFormat={{ hour: '2-digit', minute: '2-digit', hour12: false }} /></div>);
};

const EquipmentList = () => {
