//Yönetici Paneli
const AdminPanel = () => {
    const [bekleyenler, setBekleyenler] = useState([]);
    const [mesaj, setMesaj] = useState('');

    // Sayfa açılınca bekleyenleri çek
    useEffect(() => {
        fetchBekleyenler();
    }, []);

    const fetchBekleyenler = async () => {
        try {
            const response = await axios.get(`${API_URL}/auth/bekleyenler`);
            setBekleyenler(response.data);
        } catch (error) {
            console.error("Veri çekme hatası:", error);
        }
    };

    const handleOnayla = async (id) => {
        try {
            await axios.post(`${API_URL}/auth/onayla/${id}`);
            setMesaj('Kullanıcı onaylandı!');
            fetchBekleyenler(); // Listeyi yenile
            setTimeout(() => setMesaj(''), 3000); // 3 sn sonra mesajı sil
        } catch (error) {
            alert("Onaylama başarısız oldu.");
        }
    };

    const handleReddet = async (id) => {
        if(!window.confirm("Bu kullanıcıyı reddetmek (silmek) istediğine emin misin?")) return;

        try {
            await axios.delete(`${API_URL}/auth/reddet/${id}`);
            setMesaj('Kullanıcı reddedildi ve silindi.');
            fetchBekleyenler();
            setTimeout(() => setMesaj(''), 3000);
        } catch (error) {
            alert("Silme işlemi başarısız.");
        }
    };

    return (
        <div className="bg-white p-6 rounded-lg shadow">
            <h2 className="text-xl font-bold text-gray-800 mb-4 flex items-center">
                <Shield className="mr-2" /> Yönetim Paneli
            </h2>

            {mesaj && (
                <div className="bg-blue-100 text-blue-700 p-3 rounded mb-4 text-sm font-semibold">
                    {mesaj}
                </div>
            )}

            <div className="border rounded-lg overflow-hidden">
                <div className="bg-gray-50 px-4 py-3 border-b">
                    <h3 className="font-semibold text-gray-700">Onay Bekleyen Kullanıcılar</h3>
                </div>

                {bekleyenler.length === 0 ? (
                    <div className="p-6 text-center text-gray-500">
                        Şu an onay bekleyen yeni kayıt yok.
                    </div>
                ) : (
                    <table className="w-full text-sm text-left">
                        <thead className="bg-gray-50 text-gray-600 uppercase text-xs">
                            <tr>
                                <th className="px-6 py-3">Ad Soyad</th>
                                <th className="px-6 py-3">E-Posta</th>
                                <th className="px-6 py-3 text-right">İşlemler</th>
                            </tr>
                        </thead>
                        <tbody>
                            {bekleyenler.map((kisi) => (
                                <tr key={kisi.kullaniciID} className="border-b hover:bg-gray-50">
                                    <td className="px-6 py-4 font-medium text-gray-900">
                                        {kisi.ad} {kisi.soyad}
                                    </td>
                                    <td className="px-6 py-4 text-gray-600">{kisi.email}</td>
                                    <td className="px-6 py-4 text-right space-x-2">
                                        <button 
                                            onClick={() => handleOnayla(kisi.kullaniciID)}
                                            className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700 text-xs font-bold transition"
                                        >
                                            Onayla ✓
                                        </button>
                                        <button 
                                            onClick={() => handleReddet(kisi.kullaniciID)}
                                            className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700 text-xs font-bold transition"
                                        >
                                            Reddet X
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};
// --- DASHBOARD LAYOUT (Ana Panel İskeleti) ---
const DashboardLayout = ({ user, onLogout }) => {
    const [activePage, setActivePage] = useState('dashboard');

    const renderContent = () => {
        switch(activePage) {
            case 'dashboard': return <ReservationCalendar />;
            case 'equipment': return <EquipmentList />;
            case 'my-reservations': return <MyReservations user={user} />;
            case 'admin': return <AdminPanel />;
            default: return <ReservationCalendar />;
        }
    }

    return (
        <div className="flex h-screen bg-gray-100">
            <Sidebar user={user} activePage={activePage} onNavigate={setActivePage} />
            <div className="flex-1 flex flex-col">
                <Header user={user} onLogout={onLogout} />
                <main className="flex-1 p-6 overflow-y-auto">
                    {renderContent()}
                </main>
            </div>
        </div>
    );
}

// --- ANA APP ---
const App = () => {
    const [user, setUser] = useState(null);

    return (
        <Router>
            <Routes>
                {/* 1. Kayıt Ol Sayfası */}
                <Route path="/register" element={<Register />} />

                {/* 2. Login Sayfası (Kullanıcı zaten giriş yaptıysa Dashboard'a at) */}
                <Route path="/login" element={!user ? <LoginPage onLogin={setUser} /> : <Navigate to="/" />} />

                {/* 3. Ana Yol (Dashboard) - Kullanıcı yoksa Login'e at */}
                <Route path="/" element={
                    user ? (
                        <DashboardLayout user={user} onLogout={() => setUser(null)} />
                    ) : (
                        <Navigate to="/login" />
                    )
                } />
            </Routes>
        </Router>
    );
};

export default App;
